mapper.select:
select
  id, coupon_id, spu_id, spu_name
from sms_coupon_spu_relation


        and id = #{id}


        and coupon_id = #{couponId}


        and spu_id = #{spuId}


        and spu_name = #{spuName}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sms_coupon_spu_relation


        and id = #{id}


        and coupon_id = #{couponId}


        and spu_id = #{spuId}


        and spu_name = #{spuName}
