mapper.select:
select
  id, name, pic, start_time, end_time, status, click_count, url, note, sort, publisher_id, auth_id
from sms_home_adv


        and id = #{id}


        and name = #{name}


        and pic = #{pic}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and status = #{status}


        and click_count = #{clickCount}


        and url = #{url}


        and note = #{note}


        and sort = #{sort}


        and publisher_id = #{publisherId}


        and auth_id = #{authId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sms_home_adv


        and id = #{id}


        and name = #{name}


        and pic = #{pic}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and status = #{status}


        and click_count = #{clickCount}


        and url = #{url}


        and note = #{note}


        and sort = #{sort}


        and publisher_id = #{publisherId}


        and auth_id = #{authId}
