mapper.select:
select
  id, name, title, sub_title, status, url, sort, img
from sms_home_subject


        and id = #{id}


        and name = #{name}


        and title = #{title}


        and sub_title = #{subTitle}


        and status = #{status}


        and url = #{url}


        and sort = #{sort}


        and img = #{img}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sms_home_subject


        and id = #{id}


        and name = #{name}


        and title = #{title}


        and sub_title = #{subTitle}


        and status = #{status}


        and url = #{url}


        and sort = #{sort}


        and img = #{img}
