mapper.select:
select
  id, name, subject_id, spu_id, sort
from sms_home_subject_spu


        and id = #{id}


        and name = #{name}


        and subject_id = #{subjectId}


        and spu_id = #{spuId}


        and sort = #{sort}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sms_home_subject_spu


        and id = #{id}


        and name = #{name}


        and subject_id = #{subjectId}


        and spu_id = #{spuId}


        and sort = #{sort}
