mapper.select:
select
  id, title, start_time, end_time, status, create_time, user_id
from sms_seckill_promotion


        and id = #{id}


        and title = #{title}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and status = #{status}


        and create_time = #{createTime}


        and user_id = #{userId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sms_seckill_promotion


        and id = #{id}


        and title = #{title}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and status = #{status}


        and create_time = #{createTime}


        and user_id = #{userId}
