mapper.select:
select
  id, spu_id, grow_bounds, buy_bounds, work
from sms_spu_bounds


        and id = #{id}


        and spu_id = #{spuId}


        and grow_bounds = #{growBounds}


        and buy_bounds = #{buyBounds}


        and work = #{work}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sms_spu_bounds


        and id = #{id}


        and spu_id = #{spuId}


        and grow_bounds = #{growBounds}


        and buy_bounds = #{buyBounds}


        and work = #{work}
