mapper.select:
select
  id, member_id, spu_id, spu_name, spu_img, create_time
from ums_member_collect_spu


        and id = #{id}


        and member_id = #{memberId}


        and spu_id = #{spuId}


        and spu_name = #{spuName}


        and spu_img = #{spuImg}


        and create_time = #{createTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ums_member_collect_spu


        and id = #{id}


        and member_id = #{memberId}


        and spu_id = #{spuId}


        and spu_name = #{spuName}


        and spu_img = #{spuImg}


        and create_time = #{createTime}
