        and sku_attrs_vals = #{skuAttrsVals}


        and sku_count = #{skuCount}


        and sku_price = #{skuPrice}


        and sku_real_price = #{skuRealPrice}


        and reason = #{reason}


        and description述 = #{description述}


        and desc_pics = #{descPics}


