mapper.select:
select
  id, name, sort, status, create_time
from oms_order_return_reason


        and id = #{id}


        and name = #{name}


        and sort = #{sort}


        and status = #{status}


        and create_time = #{createTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from oms_order_return_reason


        and id = #{id}


        and name = #{name}


        and sort = #{sort}


        and status = #{status}


        and create_time = #{createTime}
