mapper.select:
select
  id, member_id, create_time, ip, city, login_type
from ums_member_login_log


        and id = #{id}


        and member_id = #{memberId}


        and create_time = #{createTime}


        and ip = #{ip}


        and city = #{city}


        and login_type = #{loginType}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ums_member_login_log


        and id = #{id}


        and member_id = #{memberId}


        and create_time = #{createTime}


        and ip = #{ip}


        and city = #{city}


        and login_type = #{loginType}
